fn xtime(x: u8) -> u8 {
    // performs multiplication with 'x' (or "2") in GF 2^8
    return (x << 1) ^ (((x >> 7) & 1) * 0x1b);
}

fn gf_mul(x: u8, y: u8) -> u8 {
    // multiplies two numbers in GF 2^8
    return ((y & 1) * x)
        ^ ((y >> 1 & 1) * xtime(x))
        ^ ((y >> 2 & 1) * xtime(xtime(x)))
        ^ ((y >> 3 & 1) * xtime(xtime(xtime(x))))
        ^ ((y >> 4 & 1) * xtime(xtime(xtime(xtime(x)))));
}

