pub fn xor(state: &mut [u8; 16], key: &[u8; 16]) {
    for i in 0..16 {
        state[i] ^= key[i];
    }
}

pub fn sub_bytes(state: &mut [u8; 16]) {
    for i in 0..16 {
        state[i] = SBOX[state[i] as usize];
    }
}

pub fn inv_sub_bytes(state: &mut [u8; 16]) {
    for i in 0..16 {
        state[i] = INV_SBOX[state[i] as usize];
    }
}

pub fn shift_rows(state: &mut [u8; 16]) {
    let mut temp: u8;

    // Rotate first row 1 columns to left
    temp = state[0 * 4 + 1];
    state[0 * 4 + 1] = state[1 * 4 + 1];
    state[1 * 4 + 1] = state[2 * 4 + 1];
    state[2 * 4 + 1] = state[3 * 4 + 1];
    state[3 * 4 + 1] = temp;

    // Rotate second row 2 columns to left
    temp = state[0 * 4 + 2];
    state[0 * 4 + 2] = state[2 * 4 + 2];
    state[2 * 4 + 2] = temp;

    temp = state[1 * 4 + 2];
    state[1 * 4 + 2] = state[3 * 4 + 2];
    state[3 * 4 + 2] = temp;

    // Rotate third row 3 columns to left
    temp = state[0 * 4 + 3];
    state[0 * 4 + 3] = state[3 * 4 + 3];
    state[3 * 4 + 3] = state[2 * 4 + 3];
    state[2 * 4 + 3] = state[1 * 4 + 3];
    state[1 * 4 + 3] = temp;
}

pub fn inv_shift_rows(state: &mut [u8; 16]) {
    let mut temp: u8;

    // Rotate first row 1 columns to right
    temp = state[3 * 4 + 1];
    state[3 * 4 + 1] = state[2 * 4 + 1];
    state[2 * 4 + 1] = state[1 * 4 + 1];
    state[1 * 4 + 1] = state[0 * 4 + 1];
    state[0 * 4 + 1] = temp;

    // Rotate second row 2 columns to right
    temp = state[0 * 4 + 2];
    state[0 * 4 + 2] = state[2 * 4 + 2];
    state[2 * 4 + 2] = temp;

    temp = state[1 * 4 + 2];
    state[1 * 4 + 2] = state[3 * 4 + 2];
    state[3 * 4 + 2] = temp;

    // Rotate third row 3 columns to right
    temp = state[0 + 3];
    state[0 * 4 + 3] = state[1 * 4 + 3];
    state[1 * 4 + 3] = state[2 * 4 + 3];
    state[2 * 4 + 3] = state[3 * 4 + 3];
    state[3 * 4 + 3] = temp;
}

fn xtime(x: u8) -> u8 {
    // performs multiplication with 'x' (or "2") in GF 2^8
    return (x << 1) ^ (((x >> 7) & 1) * 0x1b);
}

fn gf_mul(x: u8, y: u8) -> u8 {
    // multiplies two numbers in GF 2^8
    return ((y & 1) * x)
        ^ ((y >> 1 & 1) * xtime(x))
        ^ ((y >> 2 & 1) * xtime(xtime(x)))
        ^ ((y >> 3 & 1) * xtime(xtime(xtime(x))))
        ^ ((y >> 4 & 1) * xtime(xtime(xtime(xtime(x)))));
}

pub fn mix_columns(state: &mut [u8; 16]) {
    let mut tmp: u8;
    let mut tm: u8;
    let mut t: u8;

    for i in 0..4 {
        t = state[i * 4 + 0];

        tmp = state[i * 4 + 0] ^ state[i * 4 + 1] ^ state[i * 4 + 2] ^ state[i * 4 + 3];

        tm = state[i * 4 + 0] ^ state[i * 4 + 1];
        tm = xtime(tm);
        state[i * 4 + 0] ^= tm ^ tmp;

        tm = state[i * 4 + 1] ^ state[i * 4 + 2];
        tm = xtime(tm);
        state[i * 4 + 1] ^= tm ^ tmp;

        tm = state[i * 4 + 2] ^ state[i * 4 + 3];
        tm = xtime(tm);
        state[i * 4 + 2] ^= tm ^ tmp;

        tm = state[i * 4 + 3] ^ t;
        tm = xtime(tm);
        state[i * 4 + 3] ^= tm ^ tmp;
    }
}

pub fn inv_mix_columns(state: &mut [u8; 16]) {
    let mut a: u8;
    let mut b: u8;
    let mut c: u8;
    let mut d: u8;

    for i in 0..4 {
        a = state[i * 4 + 0];
        b = state[i * 4 + 1];
        c = state[i * 4 + 2];
        d = state[i * 4 + 3];

        state[i * 4 + 0] = gf_mul(a, 0x0e) ^ gf_mul(b, 0x0b) ^ gf_mul(c, 0x0d) ^ gf_mul(d, 0x09);
        state[i * 4 + 1] = gf_mul(a, 0x09) ^ gf_mul(b, 0x0e) ^ gf_mul(c, 0x0b) ^ gf_mul(d, 0x0d);
        state[i * 4 + 2] = gf_mul(a, 0x0d) ^ gf_mul(b, 0x09) ^ gf_mul(c, 0x0e) ^ gf_mul(d, 0x0b);
        state[i * 4 + 3] = gf_mul(a, 0x0b) ^ gf_mul(b, 0x0d) ^ gf_mul(c, 0x09) ^ gf_mul(d, 0x0e);
    }
}
use rand::rngs::OsRng;
use rand::RngCore;

use crate::aes_util::*;

const NUMROUNDS: usize = 10;
const NUMKEYS: usize = 2 * NUMROUNDS + 1;

pub trait BlockCipher {
    fn encrypt(&self, pt: [u8; 16]) -> [u8; 16];
    fn decrypt(&self, ct: [u8; 16]) -> [u8; 16];
}

pub struct ShuffledAes {
    round_keys: [[u8; 16]; NUMKEYS],
}

impl ShuffledAes {
    pub fn new() -> ShuffledAes {
        let mut round_keys = [[0u8; 16]; NUMKEYS];
        for i in 0..NUMKEYS {
            OsRng.fill_bytes(&mut round_keys[i]);
        }
        let result = ShuffledAes { round_keys };
        return result;
    }
}

impl BlockCipher for ShuffledAes {
    fn encrypt(&self, pt: [u8; 16]) -> [u8; 16] {
        let mut state = pt;

        xor(&mut state, &self.round_keys[0]);

        // sub bytes
        for key_idx in 1..NUMROUNDS + 1 {
            sub_bytes(&mut state);
            xor(&mut state, &self.round_keys[key_idx]);
        }

        // shift rows mix columns
        for key_idx in NUMROUNDS + 1..2 * NUMROUNDS + 1 {
            shift_rows(&mut state);
            mix_columns(&mut state);
            xor(&mut state, &self.round_keys[key_idx]);
        }

        return state;
    }

    fn decrypt(&self, ct: [u8; 16]) -> [u8; 16] {
        let mut state = ct;

        // shift rows mix columns
        for key_idx in (NUMROUNDS + 1..2 * NUMROUNDS + 1).rev() {
            xor(&mut state, &self.round_keys[key_idx]);
            inv_mix_columns(&mut state);
            inv_shift_rows(&mut state);
        }

        // sub bytes
        for key_idx in (1..NUMROUNDS + 1).rev() {
            xor(&mut state, &self.round_keys[key_idx]);
            inv_sub_bytes(&mut state);
        }

        xor(&mut state, &self.round_keys[0]);

        return state;
    }
}

